/**
 * Loads a user from the back-end.
 *
 * @param {number} id  The user's id.
 */
export const getUser = (id) => {
  return new Promise((resolve, reject) => {
    window.__swaggerClient.apis.user_admin.get({ contentId: id })
      .then((response) => {
        if (response && response.obj) {
          resolve({ data: response.obj.data });
        } else {
          reject({ error: 'UNKNOWN' });
        }
      })
      .catch((response) => {
        reject(response.obj);
      });
  });
};


/**
 * Deletes the specified user.
 *
 * @param {number} id  The user's id.
 */
export const deleteUser = (id) => {
  return new Promise((resolve, reject) => {
    window.__swaggerClient.apis.user_admin.delete({ contentId: id })
      .then((response) => {
        if (response && response.obj) {
          resolve({ data: response.obj.data });
        } else {
          reject({ error: 'UNKNOWN '});
        }
      })
      .catch((response) => {
        reject(response.obj);
      });
  });
};
