import * as React from 'react';


/**
 * Represents an alert that shows a message to the user and is colored based on
 * the type property.
 */
export default React.createClass({
  displayName: 'Alert',

  propTypes: {
    /**
     * The message this alert should display.
     * If not specified, the <Alert /> is not shown.
     */
    message: React.PropTypes.string,

    /**
     * The type of this <Alert />.
     *
     * These values are taken from the Bootstrap CSS.
     */
    type: React.PropTypes.oneOf(['success', 'info', 'warning', 'danger']),
  },

  getDefaultProps: () => ({
    type: 'danger',
  }),

  render() {
    const { message, type } = this.props;

    if (!message) {
      return null;
    }

    return (
      <div className={`alert alert-${type}`} role="alert">
        {message}
      </div>
    );
  }
});
