import * as React from 'react';
import { assert } from 'chai';
import { mount } from 'enzyme';

import Alert from '../';


describe('<Alert />', () => {
  it('does not render without message prop', () => {
    const wrapper = mount(<Alert />);
    assert.equal(wrapper.html(), null);
  });

  it('defaults to a danger alert', () => {
    const wrapper = mount(<Alert message="message" />);
    assert.equal(wrapper.prop('type'), 'danger');
  });

  it('can handle an alert type', () => {
    const wrapper = mount(<Alert message="message" type="success" />);
    assert.isTrue(wrapper.find('div').first().hasClass('alert-success'));
  });
});
