import * as React from 'react';
import { assert } from 'chai';
import { mount } from 'enzyme';

import Chip from '../';


describe('<Chip />', () => {
  it('calls onDelete when .chip__delete is clicked', (done) => {
    const handleDelete = () => done();
    const wrapper = mount(
      <Chip
        name="chip-name"
        value="chip-value"
        onDelete={handleDelete}
      />
    );
    wrapper.find('.chip__delete').simulate('click');
  });

  it('passes name, value to onDelete', (done) => {
    const handleDelete = (name, value) => {
      assert.equal(name, "chip-name");
      assert.equal(value, "chip-value");
      done();
    };
    const wrapper = mount(
      <Chip
        name="chip-name"
        value="chip-value"
        onDelete={handleDelete}
      />
    );
    wrapper.find('.chip__delete').simulate('click');
  });
});
