import * as React from 'react';


/**
 * Represents the header of the <ItemsControl /> displayed by a <Finder />.
 */
export default React.createClass({
  displayName: 'HeaderControl',

  propTypes: {
    columns: React.PropTypes.arrayOf(React.PropTypes.shape({
      /**
       * The text to display in this header cell.
       */
      label: React.PropTypes.string,

      /**
       * To pass in additional props, e.g. onClick.
       */
      props: React.PropTypes.object,
    })).isRequired,
  },

  render() {
    return (
      <thead>
        <tr>
          {
            this.props.columns.map((c, idx) => (
              <th key={`${idx}-${c.label}`} {...c.props}>{c.label}</th>
            ))
          }
        </tr>
      </thead>
    );
  }
});
