import * as React from 'react';


export default React.createClass({
  displayName: 'FormNotification',

  propTypes: {
    children: React.PropTypes.node,
    type: React.PropTypes.oneOf(['error', 'warning', 'success']),
  },

  render() {
    const { children, type } = this.props;
    const classes = ['form-notification', type ? `form-notification--${type}` : ''];
    return (
      <div className={classes.join(' ')}>
        {children}
      </div>
    );
  }
});
