import * as React from 'react';
import Helmet from 'react-helmet';
import SidebarToggler from './sidebar-toggler';
import settings from 'config/settings';

export default (Component) => React.createClass({
  displayName: `Stateful${Component.displayName || Component.name || 'Container'}`,

  propTypes: {
    openIssues: React.PropTypes.string
  },

  render() {
    const titleTemplate = `(${this.props.openIssues || '?'}) ${settings.platform.name}: %s`;
    const defaultTitle = titleTemplate.replace('%s', 'Dashboard');

    return (
      <div className="page">
        <Helmet titleTemplate={titleTemplate} defaultTitle={defaultTitle} />
        <div className="top-bar">
          <div className="title-wrapper text-center">
            <div className="title">Iris</div>
          </div>
          <SidebarToggler />
        </div>
        <div id="page-content-wrapper">
          <Component {...this.props} />
        </div>
      </div>
    );
  }
});
