import * as React from 'react';
import AuthApi from 'auth/api';


const reload = () => { window.location.reload(); };

const LogoutNavItem = () => (
  <li><a onClick={(e) => {
    e.preventDefault();
    AuthApi.logout()
      .then(reload)
      .catch(reload);
  }} href="#">Logout</a></li>
);


export default LogoutNavItem;
