/**
 * Removes all duplicate entries, only keeping the first occurrence.
 */
export const uniqueFilter = (value, index, array) => (
  array.indexOf(value) === index
);

/**
 * Creates an array filter that removes the specified value.
 */
export const removeValueFilter = (valueToRemove) => (value) => (
  valueToRemove !== value
);

/**
 * An array filter that rejects falsy values (i.e. '', undefined).
 */
export const truthyFilter = (value) => !!value;
