/**
 * Extracts parts of an object.
 *
 * @param {Object} object  The "full" object.
 * @param {string[]} keys  The properties that should be extracted.
 *
 * @returns {Object}  The partial object.
 */
const extractSubObject = (object, keys) => {
  const subObject = {};
  keys.forEach(key => {
    subObject[key] = object[key];
  });
  return subObject;
};

export default extractSubObject;
