import { assert } from 'chai';

import {
  getDatePart
} from '../date';

describe('helpers/date', () => {
  describe('getDatePart', () => {
    it('returns date part only', () => {
      assert.equal(getDatePart('2016-01-01T00:14:43'), '2016-01-01');
    });

    it('returns undefined for invalid date string', () => {
      assert.isUndefined(getDatePart('test'));
    });

    it('returns undefined for undefined', () => {
      assert.isUndefined(getDatePart(undefined));
    });

    it('returns undefined for null', () => {
      assert.isUndefined(getDatePart(null));
    });
  });
});
