import { assert } from 'chai';

import extractSubObject from '../extract-sub-object';


describe('helpers/extract-sub-object', () => {
  it('returns an object', () => {
    assert.isObject(extractSubObject({}, []));
  });

  it('extracts specified properties of the specified object', () => {
    const object = {
      a: 'aaa',
      b: true,
      answer: 42,
    };
    const keys = ['a', 'answer'];
    const expected = {
      a: 'aaa',
      answer: 42,
    };
    assert.deepEqual(extractSubObject(object, keys), expected);
  });

  it('can handle subobjects', () => {
    const object = {
      a: 'aaa',
      b: {
        b1: {
          b11: true,
        },
      },
    };
    const keys = ['b'];
    const expected = {
      b: {
        b1: {
          b11: true,
        },
      },
    };
    assert.deepEqual(extractSubObject(object, keys), expected);
  });
});
