export const anonymize = (text, visibleCharacters, replaceWith) => {
  if (typeof(text) === 'string') {
    const visiblePart = text.substring(0, visibleCharacters);
    const anonymizedPart = text.substring(visibleCharacters).replace(/[0-9]/g, replaceWith);
    return visiblePart + anonymizedPart;
  }
  return text;
};

export const isEmpty = (text) => {
  return text === null || typeof text === 'undefined' || text.length < 1;
};
