import * as React from 'react';
import { InputPropTypes } from 'inputs/prop-types';


/**
 * Represents a component for inputting multi-line text.
 */
export default React.createClass({
  displayName: 'MultiLineTextInput',

  propTypes: Object.assign({}, InputPropTypes, {
    placeholder: React.PropTypes.string,
    value: React.PropTypes.string,
  }),

  handleChange(e) {
    const { name, onValueChange } = this.props;
    onValueChange(name, e.target.value, true);
  },

  render() {
    const { attributes, id, placeholder, value } = this.props;
    return (
      <textarea
        {...attributes}
        className="form-control"
        value={value || ''}
        id={id}
        placeholder={placeholder}
        type="text"
        onChange={this.handleChange}
      />
    );
  }
});
