import * as React from 'react';
import _ from 'lodash';
import authenticated from 'auth';
import makePage from 'components/page';
import { anonymizeTelephone } from 'helpers/phone';
import Alert from 'components/alert';
import PetitionApi from 'petition/api';
import SupporterApi from 'supporter/api';
import Template from './template';

export default authenticated(makePage(React.createClass({
  displayName: 'Appendix',

  getInitialState() {
    return {
      error: null,
      context: {
        petition: {},
        supporters: []
      }
    };
  },

  componentWillMount() {
    this.getPetition(_.get(this.props, 'params.id'));
  },

  getPetition(petitionId) {
    if (petitionId) {
      PetitionApi.get(petitionId, 'owner').then((response) => {
        const petition = _.get(response, 'data', {});

        this.getSupporters(petitionId, _.get(petition, 'supporters.amount', 0), 50).then((responses) => {
          const supporters = [];

          responses.forEach((res) => {
            res.data.forEach((supporter) => {
              _.set(supporter, 'phone_user.telephone', anonymizeTelephone(_.get(supporter, 'phone_user.telephone'), 6, 'x'));
              supporters.push(supporter);
            });
          });

          this.setState({
            error: null,
            context: {
              petition: petition,
              supporters: supporters
            }
          });
        });
      }).catch((response) => {
        this.setState({
          error: response.error && response.error.description
        });
      });
    }
  },

  getSupporters(petitionId, amount, limit) {
    const requests = [];
    for (let i = 0; i < (amount / limit); i++) {
      requests.push(
        SupporterApi.search({
          petition: petitionId,
          resolve: 'user',
          offset: i * limit,
          limit: limit
        })
      );
    }
    return Promise.all(requests);
  },

  render() {
    const { error, context } = this.state;

    if (error) {
      return (
        <div className="container-fluid">
          <Alert message={error} />
        </div>
      );
    }

    return (
      <Template url="/templates/appendix.html" context={context} />
    );
  }
})));
