import { PropTypes } from 'react';


/**
 * Generates React prop types.
 *
 * @param {Object} petitionShape  The required shape of the petition.
 */
export default (petitionShape) => ({
  /**
   * The petition.
   */
  petition: PropTypes.shape(petitionShape).isRequired,

  /**
   * Signature: (action: string, args: any[]) => void
   *
   * This component uses the following actions:
   * - "save" with args as an array of fields to save
   */
  onAction: PropTypes.func.isRequired,

  /**
   * Signature: (key: string, value: any, valid: boolean) => void
   */
  onValueChange: PropTypes.func.isRequired,
});
