import { assert } from 'chai';
import PetitionModel from '../model';


describe('Petition Type', () => {
  it('is a function', () => {
    assert.isFunction(PetitionModel);
  });

  it('returns an object when passing an object', () => {
    assert.isObject(PetitionModel({}));
  });

  it('returns "null" for "undefined"', () => {
    assert.isNull(PetitionModel(undefined));
    assert.isNull(PetitionModel());
  });

  it('returns "null" for "null"', () => {
    assert.isNull(PetitionModel(null));
  });

  it('returns "null" for non-objects', () => {
    assert.isNull(PetitionModel(true));
    assert.isNull(PetitionModel(42));
    assert.isNull(PetitionModel('petition'));
  });
});
