import { assert } from 'chai';
import Petition from '../petition';


describe('Petition Type', () => {
  it('is a function', () => {
    assert.isFunction(Petition);
  });

  it('returns an object when passing an object', () => {
    assert.isObject(Petition({}));
  });

  it('returns "null" for "undefined"', () => {
    assert.isNull(Petition(undefined));
    assert.isNull(Petition());
  });

  it('returns "null" for "null"', () => {
    assert.isNull(Petition(null));
  });

  it('returns "null" for non-objects', () => {
    assert.isNull(Petition(true));
    assert.isNull(Petition(42));
    assert.isNull(Petition('petition'));
  });
});
