/**
 * The model of a user.
 */
const User = (userObject) => {
  if (typeof userObject !== typeof {} || userObject === null) {
    return null;
  }
  return {
    id: userObject.id,
    sso: userObject.sso || [],
    email: userObject.email,
    lastname: userObject.lastname || '',
    firstname: userObject.firstname || '',
    roles: userObject.roles || [],
    state: userObject.state || '',
    trusted: userObject.sso
              && userObject.sso.length
              && userObject.sso[0]
              && userObject.sso[0].trusted
              || false,
  };
};

export default User;
