import { assert } from 'chai';
import UserModel from '../model';


describe('UserModel', () => {
  it('is a function', () => {
    assert.isFunction(UserModel);
  });

  it('returns an object when passing an object', () => {
    assert.isObject(UserModel({}));
  });

  it('returns "null" for "undefined"', () => {
    assert.isNull(UserModel(undefined));
    assert.isNull(UserModel());
  });

  it('returns "null" for "null"', () => {
    assert.isNull(null);
  });

  it('returns "null" for non-objects', () => {
    assert.isNull(UserModel(true));
    assert.isNull(UserModel(42));
    assert.isNull(UserModel('user'));
  });
});
