import createApi from 'api/create-api';

const api = () => window.__swaggerClient.apis.weblocation_admin;
const UNKNOWN_ERROR = { error: 'UNKNOWN' };

export default Object.assign({}, createApi('weblocation_admin'), {
  resetOG: (itemId) => {
    return new Promise((resolve, reject) => {
      api().resetog({contentId: itemId}).then(response => {
        if (response && response.obj) {
          resolve(response.obj);
        } else {
          reject(UNKNOWN_ERROR);
        }
      }).catch(response => {
        if (response && response.obj) {
          reject(response.obj);
        } else {
          reject(UNKNOWN_ERROR);
        }
      });
    });
  }
});
